import React, { useState, useEffect } from "react";
import Job from "./Job";

export default function Jobs() {
	const [state, setState] = useState([]);

	const url = "/offers";
	console.log("inicia fetch");
	fetch(url)
		.then((res) => {
			console.log("state - fetched");
			return res.json();
		})
		.then((offers) => {
			console.log("offers- stated");
			setState({ offers });
		});

	return (
		<div>
			{state.offers.map((e, i) => {
				console.log(e, "offer - jobs");
				return <Job key={i} offer={e} />;
			})}
		</div>
	);
}
