import React, { useState, useEffect } from "react";
import Job from "./Job";

export default function Jobs() {
	const [state, setState] = useState({ offers: [] });

	useEffect(() => {
		const url = "/offers";
		fetch(url)
			.then((res) => {
				return res.json();
			})
			.then((offers) => {
				setTimeout(() => {
					setState({ offers });
				}, 2000);
			});
	});

	return (
		<div>
			{state.offers.map((e, i) => {
				return <Job key={i} offer={e} />;
			})}
		</div>
	);
}
